import React, {useState, useEffect} from 'react'
import Nav from '../components/Nav'
import Footer from '../components/Footer'
import './CurrencyConverter.css'

const url = 'https://blockchain.info/ticker'

const CurrencyConverter = () => {

    const [currency, setCurrency] = useState([{}])

    async function fetchTours() {
		const data = await fetch(url)
		const jsonData = await data.json()
		setCurrency(jsonData)
        console.log("Currency:")
        console.log(currency)
        console.log("jsonData:")
        console.log(jsonData)
	}

    useEffect(() => {
		fetchTours()
	}, [])

    return (
        <div>
            <Nav />
            {/* {console.log(data)} */}
            <h1>Convert any Value in any Currency to BTC</h1>
            <form>
                
                <div className="form-row">
                    <div className="form-group col-md-4">
                        <label htmlFor="inputState">Currency</label>
                        <select id="inputState" className="form-control dropdown-toggle">
                            <option selected>Choose . . .</option>
                            {/* {currency.map(curr => {
                                console.log(curr)
                                // <option>curr</option>
                            })} */}
                            
                        </select>
                    </div>
                    <div className="form-group col-md-2">
                        <label htmlFor="value">Value</label>
                        <input type="text" className="form-control" id="value" />
                    </div>
                </div>
                <button type="submit" className="btn btn-primary">Convert to BTC</button>
            </form>
            
            <Footer />
        </div>
    )
}

export default CurrencyConverter
