import React, { useState, useEffect } from 'react'
import Nav from '../components/Nav'
import Footer from '../components/Footer'
import './CurrencyConverter.css'

const url = 'https://blockchain.info/ticker'

const CurrencyConverter = () => {

    const [currency, setCurrency] = useState([{}])
    const [btc, setBtc] = useState()

    async function fetchTours() {
        const data = await fetch(url)
        const jsonData = await data.json()
        setCurrency(jsonData)
    }

    useEffect(() => {
        fetchTours()
    }, [])

    const options = []

    function handleClick(){
        console.log
    }
     
    const handleSubmit = (event) => {
        event.preventDefault();
    }

    return (
        <div>
            <Nav />
            <div className='container'>
                {/* {console.log(data)} */}
                <h1>Convert any Value in any Currency to BTC</h1>
                <form onSubmit={handleSubmit}>

                    <div className="row">
                        <div className="form-group col-md-2">
                            <label htmlFor="value">Value</label>
                            <input type="text" className="form-control" id="value" />
                        </div>
                        <div className="form-group col-md-2">

                            <label htmlFor="inputState">Currency</label>
                            <select id="inputState" className="form-control dropdown-toggle">
                                <option selected disabled>Choose . . .</option>
                                <option>USD</option>

                                {Object.entries(currency).forEach(item => {
                                    options.push(item)
                                })}
                                {options.map((opt) => {
                                     return <option>{opt[0]}</option>
                                })}

                            </select>

                        </div>
                        {/* <div className="col">
                        <input type="text" className="form-control" placeholder="Last name" />
                    </div> */}
                    </div>

                    <div className="form-row">
                        <button type="submit"  className="btn btn-primary" onClick={handleClick}>Convert</button>
                        <div className="form-group col-md-2">
                            <label htmlFor="btc">{btc} BTC</label>
                            <input type="text" className="form-control" id="btc" />
                        </div>
                    </div>

                </form>
            </div>
            <Footer />
        </div>
    )
}

export default CurrencyConverter
